@import 'tech';

header {
    padding-top: 2em;
    position: relative;

    .header-wrapper {
        display: flex;
        flex-direction: row;
        align-items: center;
        align-content: center;
        justify-content: space-between;
        z-index: 3;
        position: relative;

        nav,
        .menu {
            flex: 1;
            text-transform: uppercase;
            font-weight: 400;
            // background: $white;
            padding: 0.5em 1em;
            // border-radius: 100px;
            // box-shadow: black 3px 3px;
            // border-bottom: 2px solid $lavender;

            .active {

                &::before {
                    content: "•" !important;
                    position: absolute;
                    left: 50%;
                    top: -20px;
                    color: $orange;
                    // font-weight: 900;
                }
            }

            a {
                position: relative;
                text-decoration: none;
                color: $white;
                transition: color .3s ease;

                &:hover {
                    color: $orange;
                }


            }

            .page-link+.page-link {
                margin-left: 20px;
            }
        }

        nav {
            a {
                &::after {
                    --scale: 0;

                    content: '';
                    position: absolute;
                    left: 0;
                    right: 0;
                    top: 100%;
                    height: 3px;
                    background: $orange;
                    transform: scaleX(var(--scale));
                    transform-origin: var(--x) 50%;
                    transition: transform .3s cubic-bezier(.535, .05, .355, 1);
                }

                &:hover {
                    color: $orange;
                }

                &:hover::after {
                    --scale: 1;
                }
            }

            #jobs-link {
                position: relative;
                display: inline;

                .dropdown-wrapper {
                    filter: opacity(0);
                    position: absolute;
                    background-color: transparent;
                    min-width: 160px;
                    box-shadow: 0px 8px 16px 0px rgba(0, 0, 0, 0.2);
                    border-top: 1em solid rgba(255, 255, 255, 0);
                    z-index: 1;
                    left: 0px;
                    transition: ease all 230ms;
                    pointer-events: none;

                    .jobs-dropdown {
                        padding: 1em;
                        min-width: 200px;
                        display: flex;
                        flex-direction: column;
                        background-color: rgba($lighter-blue, 75%);
                        border: 2px solid $orange;
                        position: relative;
// border-top:8px solid $orange;
                        a+a {
                            margin-top: 10px;
                        }

                        // &::before {
                        //     position: absolute;
                        //     z-index: 2;
                        //     top: 0px;
                        //     left: 0px;
                        //     content: '';
                        //     height:5px;
                        //     width:30px;
                        //     background: $orange;
                        // }
                    }
                }

                &:hover {
                    color: $orange;

                    .dropdown-wrapper {
                        filter: opacity(1);
                        pointer-events: all;
                    }
                }
            }
        }

        .menu {
            display: flex;
            justify-content: flex-end;

            a {
                display: flex;
                flex-direction: row;
                align-items: center;

                i {
                    margin-right: 8px;
                }

                &+a {
                    margin-left: 20px;

                }
            }

        }

        svg {
            max-width: 200px;
            flex-grow: 1;
        }
    }
}

main {
    min-height: 600px;
}

.overview {
    position: relative;
    height: auto;
    background: $blue;

    .calltoaction {
        position: relative;
        margin-top: -7.5em;
