                    // padding: 0 30%;
                }

                .fa-cog {
                    position: absolute;
                    font-size: 11em;
                    line-height: 0pt;
                    left: -.5em;
                    top: 10px;
                    transition: ease all 3s;
                    color: rgba($white, 10%);
                }

                .code-symbol {
                    position: absolute;
                    color: rgba($white, 10%);
                    top: -20px;
                    left: -15px;
                    font-size: 75pt;
                    font-weight: 800;
                    letter-spacing: -8pt;
                    transition: ease all 1.5s;

                }

                .bulbs {
                    transform: rotate(180deg);
                    position: absolute;
                    font-size: 8em;
                    line-height: 0pt;
                    left: 0.7em;
                    top: 1em;
                    transition: ease all 1s;
                    color: rgba($white, 10%);

                    i {
                        position: absolute;

                        &.far {
                            filter: opacity(1);
                            transition: ease all 1s;
                        }

                        &.fas {
                            filter: opacity(0);
                            transition: ease all 1s;

                        }
                    }
                }

                &:hover {
                    border-color: $orange;

                    .fa-cog {
                        transform: rotate(70deg);
                    }

                    .code-symbol {
                        letter-spacing: -10pt;
                        transform: rotate(10deg);
                    }

                    .bulbs {
                        i {
                            transform: translateY(10px);

                            &.far {
                                filter: opacity(0);
                            }

                            &.fas {
                                filter: opacity(1);

                            }
                        }
                    }
                }

                &+.card {
                    margin-left: 1em;
                }
            }
        }
    }

