            margin: 0 auto;

            .subheader {
                width: 100%;
                margin: 1em 0 0.5em 0;
                text-align: left;
            }

            .tabs {
                // overflow: hidden;
                // box-shadow: 10px 10px $lavender;
                width: 100%;
                position: relative;

                .tab {
                    width: 100%;
                    // color: white;
                    overflow: hidden;

                    input {
                        position: absolute;
                        opacity: 0;
                        z-index: -1;
                    }

                    &-label {
                        display: flex;
                        justify-content: space-between;
                        padding: 1em;
                        background: $lighter-blue;
                        font-weight: bold;
                        cursor: pointer;
                        border: 1px rgba(255, 255, 255, 0.098) solid;

                        /* Icon */
                        &:hover {
                            background: lighten($lighter-blue, 15%);
                        }

