                    content: "";
                    position: absolute;
                    z-index: -1;
                    top: 0px;
                    left: 0px;
                    width: 100%;
                    height: 100%;
                    box-shadow: 6px 6px $lavender;
                    transition: ease all 200ms;

                }

                &::after {
                    content: "";
                    position: absolute;
                    z-index: -2;
                    top: 0px;
                    left: 0px;
                    width: 100%;
                    height: 100%;
                    box-shadow: 10px 10px $orange;
                    transition: ease all 200ms;
                }
            }

            input:checked {
                +.tab-label {
                    background: lighten($lighter-blue, 15%);

                    &::after {
                        transform: rotate(90deg);
                    }
                }

                ~.tab-content {
                    max-height: 100vh;
                    padding: 1em;
                }
            }

        }
    }
