sources.header.scss:
header {
    padding-top: 3em;
    position: relative;

    .header-wrapper {
        display: flex;
        flex-direction: row;
        align-items: center;
        align-content: center;
        justify-content: space-between;
        z-index: 3;
        position: relative;

        nav,
        .menu {
            flex: 1;
            text-transform: uppercase;
            font-weight: 400;

            .active {
                color: $orange;
                transition: ease all .3s;

                &::after {
                    --scale: 1;

                    content: '';
                    position: absolute;
                    left: 0;
                    right: 0;
                    top: 100%;
                    height: 3px;
                    background: $orange;
                    transform: scaleX(var(--scale));
                    transform-origin: var(--x) 50%;
                    transition: transform .5s cubic-bezier(.535, .05, .355, 1);
                }
            }

            a {
                position: relative;
                text-decoration: none;
                color: $white;
                transition: color .5s ease;

                &:hover {
                    color: $orange;
                }

            }

            .page-link+.page-link {
                margin-left: 20px;
            }
        }

        nav {


            a {
                transition: ease all .5s;

                &:active {
                    color: $orange;

                    &::after {
                        --scale: 1;
                    }
                }

                &::after {
                    --scale: 0;

                    content: '';
                    position: absolute;
                    left: 0;
                    right: 0;
                    top: 100%;
                    min-width: 3px;
                    height: 3px;
                    background: $orange;
                    transform: scaleX(var(--scale));
                    transform-origin: var(--x) 50%;
                    transition: transform .5s cubic-bezier(.535, .05, .355, 1);

                }

                &:hover {
                    transition-delay: 100ms;
                    color: $orange;
                }

                &:hover::after {
                    transition-delay: 100ms;
                    --scale: 1;
                }

                &:active {
                    color: $orange;

                    &::after {

                        --scale: 1;
                    }
                }
