                        text-transform: capitalize;

                    }
                }
            }

            .name,
            .email {
                width: 100%;
                display: flex;
                flex-direction: column;
                margin: 0.5em 0;
                background: darken($white, 15%);
                color: $blue;
                padding: 1em;

                p {
                    font-weight: 400;
                    font-size: 14pt;
                    margin-bottom: 5px;

                }

                .user-input {
                    position: relative;

                    input {
                        padding-left: 10px;
                        width: 100%;
                        color: $blue;
                        font-size: 20pt;
                        font-family: inherit;
                        background-color: transparent;
                        border: 2px solid transparent;
                        border-bottom-color: rgba($orange, 0.2);

                        &:focus {
                            outline: none;
                        }

                        &::placeholder {
                            color: hsla(0, 0%, 51%, 0.7);
                        }

                    }

                    span {
                        position: absolute;
                        bottom: 0;
                        left: 0;
                        right: 0;
                        height: 3px;
                        background-color: $orange;
                        transform-origin: bottom right;
                        transform: scaleX(0);
                        transition: transform 0.5s ease;
                    }

                    input:focus~span {
                        transform-origin: bottom left;
