                        &:focus {
                            outline: none;
                        }

                        &::placeholder {
                            color: hsla(0, 0%, 51%, 0.7);
                        }

                    }

                    span {
                        position: absolute;
                        background-color: $orange;
                        transition: transform 0.1s ease;

                        &.bottom,
                        &.top {
                            height: 3px;
                            left: 0;
