                            transform: scaleX(0);
                        }

                        &.left,
                        &.right {
                            width: 3px;
                            top: 0;
                            bottom: 0;
                            transform: scaleY(0);
                        }

                        &.bottom {
                            bottom: 0;
                            transform-origin: bottom right;
                        }

                        &.right {
                            right: 0;
                            transform-origin: top right;
                            transition-delay: 0.05s;
                        }

                        &.top {
                            top: 0;
