                background: darken($white, 5%);
                color: $blue;
                padding: 1em;
                position: relative;

                p {
                    font-weight: 400;
                    font-size: 14pt;
                    margin-bottom: 5px;
                }

                .user-input {
                    position: relative;

                    textarea {
                        width: 100%;
                        color: $blue;
                        font-size: 20pt;
                        background-color: transparent;
                        padding: 0.35em 0.45em;
                        border: 2px solid rgba($orange, 0.2);

                        transition: background-color 0.3s ease-in-out;
                        resize: vertical;

                        @include sm {
                            font-size: 16pt;
                        }

                        &:focus {
                            outline: none;
                        }

                        &::placeholder {
                            color: hsla(0, 0%, 51%, 0.7);
                        }

                    }

                    span {
                        position: absolute;
                        background-color: $orange;
                        transform-origin: center;
                        transition: transform 0.5s ease;

                        &.bottom,
                        &.top {
                            height: 3px;
                            left: 0;
                            right: 0;
                            transform: scaleX(0);
                        }

                        &.left,
                        &.right {
                            width: 3px;
                            top: 0;
                            bottom: 0;
                            transform: scaleY(0);
                        }

                        &.bottom {
                            bottom: 0;
                        }

                        &.right {
                            right: 0;
                        }

                        &.top {
                            top: 0;
                        }

                        &.left {
                            left: 0;
                        }
                    }

                    textarea:focus~.top,
                    textarea:focus~.bottom {
                        transform: scaleX(1);
                    }

                    textarea:focus~.left,
                    textarea:focus~.right {
                        transform: scaleY(1);
                    }

                }
            }

            .anything-else {
                border: 2px solid darken($white, 8%);

                p {
                    padding-left: 10px;
                }
            }

            button {
                margin-top: 1em;
                display: flex;
                flex-direction: row;
                align-items: center;

                i {
                    font-size: 18pt;
                    position: relative;
                    margin-left: 15px;
                    transition: ease all 230ms;
                }

                &:hover {
                    i {
                        transform: translateX(10px);
                    }

                    &::after {
                        transition: transform 0.50s ease-in-out;
                        transform: translate3d(500%, 0, 0) rotate(35deg);
                    }
                }
            }
        }

        .checkboxes {
            margin-top: 1em;

            label {
                display: block;
                position: relative;
                padding-left: 35px;
                margin-bottom: 12px;
                cursor: pointer;
                font-size: 22px;
                user-select: none;
                color: $blue;

                a {
                    color: $lighter-blue;
                    transition: ease all 230ms;

                    &:hover {
                        color: $orange;
                    }
                }
            }

            label input {
                position: absolute;
                opacity: 0;
                cursor: pointer;
                height: 0;
                width: 0;
            }

            .checkmark {
                position: absolute;
                top: 0;
                left: 0;
                height: 25px;
                width: 25px;
                background-color: darken($white, 10%);
            }

            label:hover input~.checkmark {
                background-color: darken($white, 15%);
            }

            label input:checked~.checkmark {
                background-color: $lighter-blue;
            }

            .checkmark:after {
                content: "";
                position: absolute;
                display: none;
            }

            label input:checked~.checkmark:after {
                display: block;
            }

            label .checkmark:after {
                left: 9px;
                top: 5px;
                width: 5px;
                height: 10px;
                border: solid white;
                border-width: 0 3px 3px 0;
                -webkit-transform: rotate(45deg);
                -ms-transform: rotate(45deg);
                transform: rotate(45deg);
            }
        }
    }

    .subheader {
        margin-top: 0px;
        margin-bottom: 10px;

        a {
            color: $white;
            transition: ease all 230ms;

            i {
                margin-right: 10px;
                transition: ease transform 230ms;
            }

            &:hover {

                i {
                    color: $orange;
                    transform: translateX(-5px);
                }
            }
        }
    }
}

