.overview {
    position: relative;
    height: auto;
    // background: $blue;

    .calltoaction {
        position: relative;
        margin-top: -7.5em;
        min-height: 120vh;
        z-index: 2;
        top: 0px;
        width: 100%;
        background: $lighter-blue;
        display: flex;
        flex-direction: column;
        align-items: flex-end;
        justify-content: center;
        background: url('./img/bridge_v2-min.jpg') no-repeat;
        background-size: cover;
        background-position: bottom center;

        @include sm {
            min-height: 100px;
            height: auto;
            align-items: flex-start;
            background: none;
            margin-top: 0;
            padding-top: 1em;
            flex-direction: column;
            align-items: center;
            justify-content: flex-start;
        }

        @include md {
            // background-position: center center;
            // min-height: 90vh;

            min-height: 100px;
            height: auto;
            align-items: flex-start;
            background: none;
            margin-top: 0;
            padding-top: 1em;
            flex-direction: column;
            align-items: center;
            justify-content: flex-start;
        }

        #illustration {
            background-color: $lighter-blue;
            width: 100%;
            height: auto;
            display: block;
            transform: translate3d(0, 0, 0);
            text-align: center;
            opacity: 1;
            z-index: -1;
            position: absolute;
        }

        .content-wrapper {
            height: auto;
            width: 100%;
            display: flex;
            justify-content: flex-start;

            @include md {
                justify-content: center;
                margin-bottom: 6rem;
                margin-top: 1em;
            }

            @include lg {
                width: 90vw;
            }

            .content {
                max-width: 500px;
                padding: 2em 3em;
                border: 6px solid $white;
                background: rgba($blue, 85%);

                @include sm {
                    justify-content: center;
                    background: url('./img/bridge_v2-min.jpg') no-repeat;
                    background-size: cover;
                    background-position: bottom center;
                    position: relative;
                    z-index: 1;
                    padding: 5em 2em 5em 2em;
                    width: 90vw;
                    margin: 0 auto;
                    border: 4px solid $white;
                    height: auto;

                    &::before {
                        content: '';
                        position: absolute;
                        height: 100%;
                        width: 100%;
                        background: rgba($blue, 35%);
                        top: 0px;
                        left: 0px;
                        z-index: 0;
                    }

                    #more-cta {
                        display: none;
                    }

                    h1 {
                        font-size: 24pt;
                    }
                }

                @include md {
                    justify-content: center;
                    background: url('./img/bridge_v2-min.jpg') no-repeat;
                    background-size: cover;
                    background-position: center center;
                    position: relative;
                    z-index: 1;
                    padding: 4em;
