                    left: 0.7em;
                    top: 1em;
                    transition: ease all 1s;
                    color: rgba($white, 10%);

                    i {
                        position: absolute;

                        &.far {
                            filter: opacity(1);
                            transition: ease all 1s;
                        }

                        &.fas {
                            filter: opacity(0);
                            transition: ease all 1s;

                        }
                    }
                }

                &:hover {
                    border-color: $orange;

                    .fa-cog {
                        transform: rotate(70deg);
                    }

                    .code-symbol {
                        letter-spacing: -10pt;
                        transform: rotate(10deg);
                    }

                    .bulbs {
                        i {
                            transform: translateY(10px);

                            &.far {
                                filter: opacity(0);
                            }

                            &.fas {
                                filter: opacity(1);

                            }
                        }
                    }
                }

                &+.card {
                    margin-left: 1em;

