                    @include md {
                        margin-top: 2rem;
                        transform: scale(1.45);
                    }
                }
            }
        }

        &::after {
            position: absolute;
            content: "";
            bottom: 0px;
            height: 2px;
            background: -moz-radial-gradient($orange, $blue 75%);
            background: -webkit-radial-gradient($orange, $blue 75%);
            background: radial-gradient($orange, $blue 75%);
            width: 100%;
        }
    }
}

sources.footer.scss:
footer {
    background: darken($blue, 3%);
    border-top: 4px solid lighten($lighter-blue, 5%);
    margin-top: 4em !important;

    @include sm {
        margin-top: 3em !important;
    }

    .footer-wrapper {
        display: flex;
        flex-direction: row;
        align-items: center;
        justify-content: space-around;
        padding: 2em 0;
        margin: 0 auto;

        @include sm {
            flex-wrap: wrap-reverse;
        }

        .logo {
            width: 200px;

            @include sm {
                display: none;
            }

            @include md {
                display: none;
            }

            img {
                max-width: 80px;
            }
        }

        .social {
            width: 200px;
            display: flex;
            flex-direction: row;
            align-content: center;
            justify-content: center;

            a {
                color: white;
                font-size: 20pt;
                transition: ease all 230ms;

                &+a {
                    margin-left: 20px;
                }

                &:hover {
                    color: $orange;
                }
            }
        }

        .address {
            width: 200px;
            font-weight: 300;
            color: rgba($white, 85%);

            a {
                color: rgba($white, 85%);
            }

            @include sm {
                text-align: center;
                margin-bottom: 1em;
            }
        }
    }
}
