                color: $blue;
                padding: 1em;

                p {
                    font-weight: 400;
                    font-size: 14pt;
                    margin-bottom: 5px;
                }

                .user-input {
                    position: relative;

                    textarea {
                        width: 100%;
                        color: $blue;
                        font-size: 20pt;
                        background: transparent;
                        padding: 0.35em 0.45em;
                        border: 2px solid rgba($orange, 0.2);
                        transition: background-color 0.3s ease-in-out;
                        resize: vertical;

                        &:focus {
                            outline: none;
                        }

                        &::placeholder {
                            color: hsla(0, 0%, 51%, 0.7);
                        }

                    }

                    span {
                        position: absolute;
                        background-color: $orange;
                        transition: transform 0.1s ease;

                        &.bottom,
                        &.top {
                            height: 3px;
                            left: 0;
                            right: 0;
                            transform: scaleX(0);
                        }

                        &.left,
                        &.right {
                            width: 3px;
                            top: 0;
                            bottom: 0;
                            transform: scaleY(0);
                        }

                        &.bottom {
                            bottom: 0;
                            transform-origin: bottom right;
                        }

                        &.right {
                            right: 0;
                            transform-origin: top right;
                            transition-delay: 0.05s;
                        }

                        &.top {
                            top: 0;
                            transform-origin: top left;
                            transition-delay: 0.15s;
                        }

                        &.left {
                            left: 0;
                            transform-origin: bottom left;
                            transition-delay: 0.25s;
                        }
                    }

                    textarea:focus~.bottom {
                        transform-origin: bottom left;
                        transform: scaleX(1);
                    }

                    textarea:focus~.right {
                        transform-origin: bottom right;
                        transform: scaleY(1);
                    }

                    textarea:focus~.top {
                        transform-origin: top right;
                        transform: scaleX(1);
                    }

                    textarea:focus~.left {
                        transform-origin: top left;
                        transform: scaleY(1);
                    }

                }
            }

            button {
                margin-top: 1em;
                display: flex;
                flex-direction: row;
                align-items: center;

                i {
                    font-size: 18pt;
                    position: relative;
                    margin-left: 15px;
