sources.tech.scss:
// normalize!
@import 'normalize';
@import url('https://unpkg.com/aos@2.3.1/dist/aos.css');
@import url('https://cdnjs.cloudflare.com/ajax/libs/limonte-sweetalert2/8.11.8/sweetalert2.min.css');
// fonts!
@import url('https://fonts.googleapis.com/css?family=Lato:100,100i,300,300i,400,400i,700,700i,900,900i|Raleway:100,100i,200,200i,300,300i,400,400i,500,500i,600,600i,700,700i,800,800i,900,900i&display=swap');

// colors!
$blue: #0b0237;
$lighter-blue: #1f2478;
$lavender: #6249ae;
$orange: #e37038;
$white: rgb(245, 245, 245);

// page transitions!
.transition-fade {
    transition: 0.4s;
    opacity: 1;
}

.transition-corn,
.transition-cta,
.transition-dog {
    transition: 0.6s;
    opacity: 1;
}

html {
    scroll-behavior: smooth;

}

html.is-animating {
    .transition-fade {
        opacity: 0;
        transform: translateY(40px);

        // .bg {
        //     transform: translateY(-40px) !important;
        // }
    }

    .transition-corn {
        opacity: 0;
        transform: translateY(40px);

    }

    .transition-cta {
        opacity: 0;
        transform: translateX(-40px);

    }

    .transition-dog {
        opacity: 0;
        transform: translateY(-200px);

    }
}

// structure tech!
body {
    background: $blue;
    color: $white;
    box-sizing: border-box;
    margin: 0 auto;
    overflow-y: scroll;
    overflow-x: hidden !important;
    position: relative;
}

* {
    font-family: 'Lato', sans-serif;
    text-decoration: none;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    margin: 0px;

    ::-moz-selection {
        background: $orange;
    }

    ::selection {
        background: $orange;
    }
}

h1,
h2,
h3,
h4,
h5,
h5 {
    font-family: 'Raleway', sans-serif;
    text-transform: uppercase;
}

button {
    z-index: 1;
    position: relative;
    font-size: 14pt;
    font-family: inherit;
    color: white;
    padding: 0.5em 1em;
    outline: none;
    border: none;
    background-color: $lighter-blue;
    overflow: hidden;
    cursor: pointer !important;
    padding: 1em 2em;
    transition: ease background 190ms;

    &::after {
        content: '';
        z-index: -1;
        // background-color: hsla(0, 0%, 100%, 0.2);
        background: rgba($white, 10%);
        position: absolute;
        top: -50%;
        bottom: -50%;
        width: 2.8em;
        transform: translate3d(-525%, 0, 0) rotate(35deg);
    }

    &:hover {
        cursor: pointer !important;

        background: lighten($lighter-blue, 5%);

        &::after {

            transition: transform 0.50s ease-in-out;
            transform: translate3d(200%, 0, 0) rotate(35deg);
        }
    }
}

.container-home,
.container-head {
    width: 90vw;
    max-width: 1300px;
    margin: auto;
}

.container {
    width: 90vw;
    max-width: 1050px;
    margin: auto;
}

.subheader {
    width: 100%;
    margin: 1em 0 0.5em 0;
    text-align: left;
}

section {

    h2 {
        margin-bottom: 1em;
        font-size: 24pt;
        text-align: left;

        .head_orange {
            color: $orange;
        }
    }

    hr {
        border-color: $orange;
        // margin-bottom:10px;
    }
}

section+section {
    margin-top: 3em !important;
    border-bottom: 2px solid linear-gradient(to bottom, red, rgba(0, 0, 0, 0)) 1 100%;


}

.loading {
    position: absolute;
    z-index: -10 !important;
    top: 40vh;
    left: 48%;

    svg {
        width: 3.75em;
        transform-origin: center;
        animation: rotate 2s linear infinite;
    }

    circle {
        fill: none;
        stroke: $orange;
        stroke-width: 2;
        stroke-dasharray: 1, 200;
        stroke-dashoffset: 0;
        stroke-linecap: round;
        animation: dash 1.5s ease-in-out infinite;
    }

    @keyframes rotate {
        100% {
            transform: rotate(360deg);
        }
    }

    @keyframes dash {
        0% {
            stroke-dasharray: 1, 200;
            stroke-dashoffset: 0;
        }

        50% {
            stroke-dasharray: 90, 200;
            stroke-dashoffset: -35px;
        }

        100% {
            stroke-dashoffset: -125px;
        }
    }
}

/* KEYFRAMES */
@keyframes spin {

    50%,
    100% {
        transform: rotate(360deg);
    }
}

@keyframes fill {

    25%,
    50% {
        transform: scaleY(0);
    }

    100% {
        transform: scaleY(1);
    }
}

@-webkit-keyframes animateCloud {
    0% {
        margin-left: -140vw;
    }

    100% {
        margin-left: 120vw;
    }
}

@-moz-keyframes animateCloud {
    0% {
        margin-left: -140vw;
    }

    100% {
        margin-left: 120vw;
    }
}

@keyframes animateCloud {
    0% {
        margin-left: -140vw;
    }

    100% {
        margin-left: 140vw;
    }
}

/* ANIMATIONS */

.x1 {
    -webkit-animation: animateCloud 70s linear infinite;
    -moz-animation: animateCloud 70s linear infinite;
    animation: animateCloud 70s linear infinite;
    animation-delay: -10s;

    -webkit-transform: scale(0.65);
    -moz-transform: scale(0.65);
    transform: scale(0.65);
}

.x2 {
    -webkit-animation: animateCloud 75s linear infinite;
    -moz-animation: animateCloud 75s linear infinite;
    animation: animateCloud 75s linear infinite;

    -webkit-transform: scale(0.3);
    -moz-transform: scale(0.3);
    transform: scale(0.3);
    animation-delay: -20s;

}

.x3 {
    -webkit-animation: animateCloud 70s linear infinite;
    -moz-animation: animateCloud 70s linear infinite;
    animation: animateCloud 70s linear infinite;

    -webkit-transform: scale(0.5);
    -moz-transform: scale(0.5);
    transform: scale(0.5);
    animation-delay: -30s;

}

.x4 {
    -webkit-animation: animateCloud 60s linear infinite;
    -moz-animation: animateCloud 60s linear infinite;
    animation: animateCloud 60s linear infinite;

    -webkit-transform: scale(0.4);
    -moz-transform: scale(0.4);
    transform: scale(0.4);
    animation-delay: -45s;

}

.x5 {
    -webkit-animation: animateCloud 50s linear infinite;
    -moz-animation: animateCloud 50s linear infinite;
    animation: animateCloud 50s linear infinite;

    -webkit-transform: scale(0.55);
    -moz-transform: scale(0.55);
    transform: scale(0.55);
    animation-delay: -30s;

}

/* OBJECTS */

.cloud {
    filter: opacity(10%);
    background: #fff;

    -webkit-border-radius: 100px;
    -moz-border-radius: 100px;
    border-radius: 100px;

    height: 120px;
    position: absolute;
    width: 350px;
    transform: scale(1, 0.5);

    &:after,
    &:before {
        background: #fff;
        content: '';
        position: absolute;
        z-indeX: -1;
    }

    &:after {
        -webkit-border-radius: 100px;
        -moz-border-radius: 100px;
        border-radius: 100px;

        height: 100px;
        left: 50px;
        top: -50px;
        width: 100px;
    }

    &:before {
        -webkit-border-radius: 200px;
        -moz-border-radius: 200px;
        border-radius: 200px;

        width: 180px;
        height: 180px;
        right: 50px;
        top: -90px;
    }
}

.clouds {
    top: 20px !important;
    left: 0;
    // margin-top: -36em;
    position: absolute;
    padding-top: 1em;
    right: 0;
    top: 0;
    z-index: 3;
    transform: scale(0.65);
    pointer-events: none;
}

// popup styles!




.popup {
    border-radius: 0px !important;
    background: $white !important;

    .title {
        font-family: 'Raleway',
            sans-serif !important;

    }

    .swal2-success-ring {
        border-color: rgba($blue, 70%) !important;

    }

    .popup-confirm {
        background: $lighter-blue !important;
        border: none !important;
        border-radius: 0px !important;
        padding: 1em 1.5em !important;
        font-size: 14pt !important;

        &:hover {
            background: lighten($lighter-blue, 5%) !important;
        }
    }
}
