@import 'tech';
@import 'header';
@import 'overview';
@import 'footer';

main {
    min-height: 600px;
    @include sm {
        padding-top:3em;
    }
}

.who-we-are {
    position: relative;
    min-height: 100vh;
    z-index: 2;
    top: 0px;
    width: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    background: $blue;

    .bg {
        position: absolute;
        z-index: -4;
        top: -7rem;
        left: 0;
        width: 105%;
        min-height: 550px;
        filter: opacity(1);
        background: $blue;
        border-bottom: 1px solid darken($orange, 15%);

        &::before {
            content: "";
            position: absolute;
            z-index: -3;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: url('./img/groundfloor-min.jpg') no-repeat;
            background-size: 100%;
            background-position: top -40em center;
            background-attachment: absolute;
            filter: opacity(0.3);
        }

        &::after {
            content: "";
            position: absolute;
            z-index: -2;
            left: 0;
            width: 100%;
            height: 100%;
            background: -moz-linear-gradient(to bottom, rgba($blue, 00%) 100%, $blue);
            background: -webkit-linear-gradient(to bottom, rgba($blue, 00%) 100%, $blue);
            background: linear-gradient(to bottom, rgba($blue, 00%) 100%, $blue);
        }
    }

    .staff {
        position: relative;
        margin-top: 5em !important;
        display: flex;
        flex-direction: column;
        max-width: 1100px;
        margin: 0 auto;

        hr {
            width: 90%;
            margin: 0 auto;
        }

        h1 {
            text-align: center;
            align-self: center;
        }

        .staff-cards {
            display: flex;
            flex-direction: row;
            flex-wrap: wrap;
            justify-content: center;

            .card {
                position: relative;
                flex: 1 33%;
                height: 300px;
                overflow: hidden;
                max-width: 330px;
                margin: 1em;

                @include lg {
                    flex: 1 30%;
                }

                h3 {
                    position: absolute;
                    z-index: 2;
                    bottom: 20px;
                    left: 20px;
                    transition: ease all 230ms;
                    background: $orange;
                    padding: 10px;
                }

                h5 {
                    font-size: 11pt;
                    font-weight: 200;
                    position: absolute;
                    z-index: 3;
                    bottom: 20px;
                    left: 20px;
                    transform: translateY(130px);
                    transition: ease all 230ms;
                    text-shadow: 2px 2px black;
                }

                img {
                    position: relative;
                    z-index: 1;
                    object-fit: contain;
                    width: 100%;
                }

                &:hover {
                    h3 {
                        transform: translateY(-30px);
                    }

                    h5 {
                        transform: translateY(0px);
                    }
                }
            }
        }


    }
}

.jobs-wrapper {
    position: relative;
    z-index: 2;
    top: 0px;
    width: 100%;
    display: flex;
    flex-direction: column;
    background: $blue;

    .bg {
        position: absolute;
        z-index: -4;
        top: -7rem;
        left: 0;
        width: 100%;
        min-height: 550px;
        filter: opacity(1);
        border-bottom: 1px solid darken($orange, 15%);

        &::before {
            content: "";
            position: absolute;
            // z-index: -3;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: url('./img/front-full-min.jpg') no-repeat;
            background-size: 100%;
            background-position: top -70px right;
            filter: opacity(0.3);
            background-attachment: absolute;
        }

        &::after {
            content: "";
            position: absolute;
            left: 0;
            width: 100%;
            height: 100%;
            background: -moz-linear-gradient(to bottom, rgba($blue, 00%) 100%, $blue);
            background: -webkit-linear-gradient(to bottom, rgba($blue, 00%) 100%, $blue);
            background: linear-gradient(to bottom, rgba($blue, 00%) 100%, $blue);
        }
    }

    .jobs {
        padding-top: 4.5rem;
        position: relative;
        display: flex;
        flex-direction: column;
        max-width: 1050px;
        margin: 0 auto;

        hr {
            width: 90%;
            margin: 0 auto;
        }

        h1 {
            text-align: center;
            align-self: center;
        }

        p {
            text-align: center;
            font-weight: 300;
        }

        .job-cards {
            display: flex;
            flex-direction: column;
            align-items: center;
            width: 1050px;
            margin: 0 auto;



            .job {
                position: relative;
                display: flex;
                flex-direction: row;
                justify-content: space-between;
                align-items: center;
                background: white;
                color: $blue;
                width: 100%;
                padding: 1.5em 1.5em;
                transition: ease all 200ms;

                &:nth-child(2n) {
                    background: rgb(233, 233, 233);
                }

                &::before {
                    content: "";
                    position: absolute;
                    z-index: -1;
                    top: 0px;
                    left: 0px;
                    width: 100%;
                    height: 100%;
                    box-shadow: 0px 0px;
                    transition: ease all 200ms;
                }

                &::after {
                    content: "";
                    position: absolute;
                    z-index: -2;
                    top: 0px;
                    left: 0px;
                    width: 100%;
                    height: 100%;
                    transition: ease all 200ms;
                }

                &:hover {
                    transform: translate(-10px, -10px);
                    box-shadow: 0px 0px;
                    cursor: default;

                    &::before {
                        box-shadow: 6px 6px $lavender;
                    }

                    &::after {
                        box-shadow: 10px 10px $orange;
                    }
                }

                .job-left {
                    display: flex;
                    flex-direction: row;
                    align-items: center;
                    justify-content: space-between;
                    align-content: center;

                    span {
                        background: rgba($lighter-blue, 75%);
                        color: $white;
                        padding: 5px 8px;
                        margin: 10px 0px;
                        font-size: 10pt;

                        i {
                            font-size: inherit;
                            border-radius: 0px;
                            padding: 0px;
                            display: inline;
                            border: none;
                            margin-right: 5px;
                            color: rgba($orange, 85%);
                        }

                        &+span {
                            margin-left: 10px;
                        }
                    }

                    i {
                        display: flex;
                        flex-direction: row;
                        justify-content: center;
                        align-items: center;
                        font-size: 30pt;
                        margin-right: 20px;
                        padding: 5px;
                        border: 2px solid $orange;
                        border-radius: 100px;
                        width: 60px;
                        height: 60px;
                    }

                    .job-info {

                        h3 {
                            margin-top: 0px;
                            margin-bottom: 10px;
                            font-size: 15pt;
                        }
                    }
                }
            }
        }
    }

    .talent {
        padding-bottom: 1.5rem;
        position: relative;
        display: flex;
        flex-direction: column;
        max-width: 1050px;
        margin: 0 auto;

        .talent-jobs {
            display: flex;
            flex-direction: column;
            align-items: center;
            width: 1050px;
            margin: 0 auto;

            .subheader {
                width: 100%;
                margin: 1em 0 0.5em 0;
                text-align: left;
            }

            .tabs {
                width: 100%;
                position: relative;
                background: linear-gradient(140deg, $lighter-blue, lighten($blue, 5%));

                .tab {
                    width: 100%;
                    overflow: hidden;

                    input {
                        position: absolute;
                        opacity: 0;
                        z-index: -1;
                    }

                    &-label {
                        display: flex;
                        justify-content: space-between;
                        padding: 1em;
                        // background: $lighter-blue;
                        background: transparent;
                        font-weight: bold;
                        cursor: pointer;
                        border: 1px rgba(255, 255, 255, 0.098) solid;

                        &:hover {
                            background: rgba($lighter-blue, 95%);
                        }

                        &::after {
                            content: "\276F";
                            width: 1em;
                            height: 1em;
                            text-align: center;
                            transition: ease all 230ms;
                        }
                    }

                    &-content {
                        display: flex;
                        flex-wrap: wrap;
                        align-items: stretch;

                        max-height: 0;

                        padding: 0 1em;
                        color: $blue;
                        background: white;
                        transition: ease all 500ms;

                        a {
                            flex: 1 30%;
                            font-size: 10pt;
                            font-weight: 400;
                            padding: 10px;
                            background: rgb(210, 210, 210);
                            color: $blue;
                            margin: 10px;

                            &:hover {
                                background: rgb(225, 225, 225);
                            }
                        }
                    }

                    &-close {
                        display: flex;
                        justify-content: flex-end;
                        padding: 1em;
                        font-size: 0.75em;
                        background: $blue;
                        cursor: pointer;

                        &:hover {
                            background: darken($orange, 10%);
                        }
                    }
                }

                &::before {
                    content: "";
                    position: absolute;
                    z-index: -1;
                    top: 0px;
                    left: 0px;
                    width: 100%;
                    height: 100%;
                    // box-shadow: 6px 6px $lavender;
                    transition: ease all 200ms;

                }

                &::after {
                    content: "";
                    position: absolute;
                    z-index: -2;
                    top: 0px;
                    left: 0px;
                    width: 100%;
                    height: 100%;
                    // box-shadow: 10px 10px $orange;
                    transition: ease all 200ms;
                }
            }

            input:checked {
                +.tab-label {
                    background: rgba($lighter-blue, 95%);

                    &::after {
                        transform: rotate(90deg);
                    }
                }

                ~.tab-content {
                    max-height: 100vh;
                    padding: 1em;
                }
            }

        }
    }
}

