sources.tech.scss:
// normalize!
@import 'normalize';
@import url('https://unpkg.com/aos@2.3.1/dist/aos.css');
@import url('https://cdnjs.cloudflare.com/ajax/libs/limonte-sweetalert2/8.11.8/sweetalert2.min.css');
// @import url('https://cdnjs.cloudflare.com/ajax/libs/awesomplete/1.1.4/awesomplete.css');
// fonts!
@import url('https://fonts.googleapis.com/css?family=Lato:100,100i,300,300i,400,400i,700,700i,900,900i|Raleway:100,100i,200,200i,300,300i,400,400i,500,500i,600,600i,700,700i,800,800i,900,900i&display=swap');

// colors!
$blue: #0b0237;
$lighter-blue: #1f2478;
$lavender: #6249ae;
$orange: #e37038;
$white: rgb(245, 245, 245);

// responsive mixins
    // define variables
    $screen-sm-max: 768px;
    $screen-md-min: 769px;
    $screen-md-max: 1199px;
    $screen-lg-min: 1200px;
    $screen-lg-max: 1600px;
    $screen-xl-min: 1601px;
    // specify mixins
    @mixin sm {
        @media (max-width: #{$screen-sm-max}) {
            @content;
        }
    }

    @mixin md {
        @media (min-width: #{$screen-md-min}) and (max-width: #{$screen-md-max}) {
            @content;
        }
    }

    @mixin lg {
        @media (min-width: #{$screen-lg-min}) and (max-width: #{$screen-lg-max}) {
            @content;
        }
    }

    @mixin xl {
        @media (min-width: #{$screen-xl-min}) {
            @content;
        }
    }
    @mixin tab {
        @media (min-width: 1023px) and (min-height:800px) {
            @content;
        }
    }

// page transitions!
.transition-fade {
    transition: 0.4s;
    opacity: 1;
}

.transition-corn,
.transition-cta,
.transition-dog {
    transition: 0.6s;
    opacity: 1;
}

html {
    scroll-behavior: smooth;

}

html.is-animating {
    .transition-fade {
        opacity: 0;
        transform: translateY(40px);

        // .bg {
        //     transform: translateY(-40px) !important;
        // }
    }

    .transition-corn {
        opacity: 0;
        transform: translateY(40px);

    }

    .transition-cta {
        opacity: 0;
        transform: translateX(-40px);

    }

    .transition-dog {
        opacity: 0;
        transform: translateY(-200px);

    }
}

// structure tech!
body {
    background: $blue;
    color: $white;
    box-sizing: border-box;
    margin: 0 auto;
    overflow-y: scroll;
    position: relative;
    @include sm {
        max-width:100vw;
        overflow-x:hidden;
    }
}

* {
    font-family: 'Lato', sans-serif;
    text-decoration: none;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    margin: 0px;

    ::-moz-selection {
        background: $orange;
    }

    ::selection {
        background: $orange;
    }
}

h1,
h2,
h3,
h4,
h5,
h5 {
    font-family: 'Raleway', sans-serif;
    text-transform: uppercase;
}

button {
    z-index: 1;
    position: relative;
    font-size: 14pt;
    font-family: inherit;
    color: white;
    padding: 0.5em 1em;
    outline: none;
    border: none;
    background-color: $lighter-blue;
    overflow: hidden;
    cursor: pointer !important;
    padding: 1em 2em;
    transition: ease background 190ms;

    &::after {
        content: '';
        z-index: -1;
        // background-color: hsla(0, 0%, 100%, 0.2);
        background: rgba($white, 10%);
        position: absolute;
        top: -50%;
        bottom: -50%;
        width: 2.8em;
        transform: translate3d(-525%, 0, 0) rotate(35deg);
    }

    &:hover {
        cursor: pointer !important;

        background: lighten($lighter-blue, 5%);

        &::after {

            transition: transform 0.50s ease-in-out;
            transform: translate3d(200%, 0, 0) rotate(35deg);
        }
    }
}

.container-home,
.container-head {
    width: 90vw;
    max-width: 1300px;
    margin: auto;
}

.container {
    width: 90vw;
    max-width: 1050px;
    margin: auto;
}

.subheader {
    width: 100%;
    margin: 1em 0 0.5em 0;
    text-align: left;
}

section {

    h2 {
        margin-bottom: 1em;
        font-size: 24pt;
        text-align: left;

        .head_orange {
            color: $orange;
        }
    }

    hr {
        border-color: $orange;
        // margin-bottom:10px;
    }
}

section+section {
    margin-top: 3em !important;
    border-bottom: 2px solid linear-gradient(to bottom, red, rgba(0, 0, 0, 0)) 1 100%;


}
.apply-cta {
    margin-top: 10px;
    width: 100%;
    display: flex;
    flex-direction: row;
    justify-content: space-around;
    align-items: center;
    flex-wrap: wrap;
    h2,
    p {
        text-align: center;
    }

}
.loading {
    position: absolute;
    z-index: -10 !important;
    top: 40vh;
    left: 48%;

    svg {
        width: 3.75em;
        transform-origin: center;
        animation: rotate 2s linear infinite;
    }

    circle {
        fill: none;
        stroke: $orange;
        stroke-width: 2;
        stroke-dasharray: 1, 200;
        stroke-dashoffset: 0;
        stroke-linecap: round;
        animation: dash 1.5s ease-in-out infinite;
    }

    @keyframes rotate {
        100% {
            transform: rotate(360deg);
        }
    }

    @keyframes dash {
        0% {
            stroke-dasharray: 1, 200;
            stroke-dashoffset: 0;
        }

        50% {
            stroke-dasharray: 90, 200;
            stroke-dashoffset: -35px;
        }

        100% {
            stroke-dashoffset: -125px;
        }
    }
}

/* KEYFRAMES */
@keyframes spin {

    50%,
    100% {
        transform: rotate(360deg);
    }
}

@keyframes fill {

    25%,
    50% {
        transform: scaleY(0);
    }

    100% {
        transform: scaleY(1);
    }
}

@-webkit-keyframes animateCloud {
    0% {
        margin-left: -140vw;
    }

    100% {
        margin-left: 120vw;
    }
}

@-moz-keyframes animateCloud {
    0% {
        margin-left: -140vw;
    }

    100% {
        margin-left: 120vw;
    }
}

@keyframes animateCloud {
    0% {
        margin-left: -140vw;
    }

    100% {
        margin-left: 140vw;
    }
}

/* ANIMATIONS */

.x1 {
    -webkit-animation: animateCloud 70s linear infinite;
    -moz-animation: animateCloud 70s linear infinite;
    animation: animateCloud 70s linear infinite;
    animation-delay: -10s;

    -webkit-transform: scale(0.65);
    -moz-transform: scale(0.65);
    transform: scale(0.65);
}

.x2 {
    -webkit-animation: animateCloud 75s linear infinite;
    -moz-animation: animateCloud 75s linear infinite;
    animation: animateCloud 75s linear infinite;

    -webkit-transform: scale(0.3);
    -moz-transform: scale(0.3);
    transform: scale(0.3);
    animation-delay: -20s;

}

.x3 {
    -webkit-animation: animateCloud 70s linear infinite;
    -moz-animation: animateCloud 70s linear infinite;
    animation: animateCloud 70s linear infinite;

    -webkit-transform: scale(0.5);
    -moz-transform: scale(0.5);
    transform: scale(0.5);
    animation-delay: -30s;

}

.x4 {
    -webkit-animation: animateCloud 60s linear infinite;
    -moz-animation: animateCloud 60s linear infinite;
    animation: animateCloud 60s linear infinite;

    -webkit-transform: scale(0.4);
    -moz-transform: scale(0.4);
    transform: scale(0.4);
    animation-delay: -45s;

}

.x5 {
    -webkit-animation: animateCloud 50s linear infinite;
    -moz-animation: animateCloud 50s linear infinite;
    animation: animateCloud 50s linear infinite;

    -webkit-transform: scale(0.55);
    -moz-transform: scale(0.55);
    transform: scale(0.55);
    animation-delay: -30s;

}

/* OBJECTS */

.cloud {
    filter: opacity(10%);
    background: #fff;

    -webkit-border-radius: 100px;
    -moz-border-radius: 100px;
    border-radius: 100px;

    height: 120px;
    position: absolute;
    width: 350px;
    transform: scale(1, 0.5);

    &:after,
    &:before {
        background: #fff;
        content: '';
        position: absolute;
        z-indeX: -1;
    }

    &:after {
        -webkit-border-radius: 100px;
        -moz-border-radius: 100px;
        border-radius: 100px;

        height: 100px;
        left: 50px;
        top: -50px;
        width: 100px;
    }

    &:before {
        -webkit-border-radius: 200px;
        -moz-border-radius: 200px;
        border-radius: 200px;

        width: 180px;
        height: 180px;
        right: 50px;
        top: -90px;
    }
}

.clouds {
    top: 20px !important;
    left: 0;
    // margin-top: -36em;
    position: absolute;
    padding-top: 0.8rem;
    right: 0;
    top: 0;
    z-index: 3;
    transform: scale(0.65);
    pointer-events: none;
    overflow-x: hidden !important;

}

// popup styles!

.job-popup {
    border-radius: 0px !important;
    .popup-title,
    h2 {
        font-family: 'Lato',
            sans-serif;
        text-transform: uppercase;
        color: $blue;
        // background: $orange;
        padding:5px 8px;
        font-size:20pt !important;
        text-align: center !important;
        align-self: center;
    }

    .popup-icon {
        max-width: 81px !important;
        max-height: 90px !important;
    }

    .popup-content {
        .paragraph {
            text-align: left;
            &+.paragraph {
                margin-top: 10px;
            }
        }

        .insurance-details {
            text-align: left;
            margin-top: 10px;

            .paragraph {
                margin-bottom: 5px;
            }

            a {
                display: block;
                width: 100%;
                padding: 8px 12px;
                background: rgb(210, 210, 210);
                color: $blue;
                transition: ease all 230ms;

                &:hover {
                    color: $lighter-blue;
                    background: rgb(225, 225, 225);
                }

                &+a {
                    margin-top: 5px;
                }

                i {
                    margin-right: 10px;

                }
            }
        }
    }

    .popup-confirm {
        background: $lighter-blue !important;
        border: none !important;
        border-radius: 0px !important;
        padding: 1em 1.5em !important;
        font-size: 14pt !important;

        &:hover {
            background: lighten($lighter-blue, 5%) !important;
        }
    }
}

.contact-popup {
    border-radius: 0px !important;
    background: $white !important;
// border: 4px solid $orange !important;
    .popup-title,
    h2 {
        font-family: 'Raleway',
            sans-serif;
        text-transform: uppercase;
        color: $blue;

    }



    .swal2-success-ring {
        border-color: rgba($blue, 70%) !important;

    }

    .popup-confirm {
        background: $lighter-blue !important;
        border: none !important;
        border-radius: 0px !important;
        padding: 1em 1.5em !important;
        font-size: 14pt !important;

        &:hover {
            background: lighten($lighter-blue, 5%) !important;
        }
    }
}

sources.header.scss:
header {
    padding-top: 3em;
    position: relative;

    .header-wrapper {
        display: flex;
        flex-direction: row;
        align-items: center;
        align-content: center;
        justify-content: space-between;
        z-index: 3;
        position: relative;

        nav,
        .menu {
            flex: 1;
            text-transform: uppercase;
            font-weight: 400;

            @include sm {
                flex: none;
            }

            .active {
                color: $orange;
                transition: ease all .3s;

                &::after {
                    --scale: 1;

                    content: '';
                    position: absolute;
                    left: 0;
                    right: 0;
                    top: 100%;
                    height: 3px;
                    background: $orange;
                    transform: scaleX(var(--scale));
                    transform-origin: var(--x) 50%;
                    transition: transform .5s cubic-bezier(.535, .05, .355, 1);
                }
            }

            a {
                position: relative;
                text-decoration: none;
                color: $white;
                transition: color .5s ease;

                &:hover {
                    color: $orange;
                }

            }

            .page-link+.page-link {
                margin-left: 20px;
            }
        }

        nav {


            a {
                transition: ease all .5s;

                &:active {
                    color: $orange;

                    &::after {
                        --scale: 1;
                    }
                }

                &::after {
                    --scale: 0;

                    content: '';
                    position: absolute;
                    left: 0;
                    right: 0;
                    top: 100%;
                    min-width: 3px;
                    height: 3px;
                    background: $orange;
                    transform: scaleX(var(--scale));
                    transform-origin: var(--x) 50%;
                    transition: transform .5s cubic-bezier(.535, .05, .355, 1);

                }

                &:hover {
                    transition-delay: 100ms;
                    color: $orange;
                }

                &:hover::after {
                    transition-delay: 100ms;
                    --scale: 1;
                }

                &:active {
                    color: $orange;

                    &::after {

                        --scale: 1;
                    }
                }
            }

            #jobs-dropdown {
                position: relative;
                display: inline;

                .dropdown-wrapper {
                    filter: opacity(0);
                    position: absolute;
                    background-color: transparent;
                    min-width: 160px;
                    box-shadow: 0px 8px 16px 0px rgba(0, 0, 0, 0.2);
                    border-top: 1em solid rgba(255, 255, 255, 0);
                    z-index: 1;
                    left: 0px;
                    transition: ease all 230ms;
                    pointer-events: none;


                    .jobs-menu {
                        padding: 1em;
                        min-width: 200px;
                        display: flex;
                        flex-direction: column;
                        background-color: rgba($lighter-blue, 75%);
                        border: 2px solid $orange;
                        position: relative;

                        a {
                            transition: ease all 230ms;
                            color: $white;

                            &:hover {
                                color: $orange;
                            }

                            &::after {
                                content: '';
                                position: absolute;
                                height: 0px;
                                transform-origin: 0%;
                            }

                            &+a {
                                margin-top: 10px;
                            }
                        }

                    }
                }

                &:hover {
                    color: $orange;

                    .dropdown-wrapper {
                        filter: opacity(1);
                        pointer-events: all;
                    }
                }
            }
        }

        .menu {
            display: flex;
            justify-content: flex-end;

            a {
                display: flex;
                flex-direction: row;
                align-items: center;
                transition: ease all 230ms;

                i {
                    margin-right: 8px;
                }

                &+a {
                    margin-left: 20px;

                }
            }

            .apply-link {
                z-index: 1;
                position: relative;
                font-size: inherit;
                font-family: inherit;
                color: white;
                padding: 0.8em 1em;
                outline: none;
                border: none;
                background-color: none;
                overflow: hidden;
                cursor: pointer;
                border: 2px solid $white;

                &::after {
                    content: '';
                    z-index: -1;
                    background-color: hsla(0, 0%, 100%, 0.1);
                    position: absolute;
                    top: -50%;
                    bottom: -50%;
                    width: 1.25em;
                    transform: translate3d(-525%, 0, 0) rotate(35deg);
                }

                &:hover {
                    background: rba($white, 15%);

                    &::after {
                        transition: transform 0.45s ease-in-out;
                        transform: translate3d(800%, 0, 0) rotate(35deg);
                    }
                }
            }
        }

        .menu-apply {
            display: flex;
            justify-content: flex-end;
            flex: 1;
            text-transform: uppercase;
            font-weight: 400;

            #contact-link {
                position: relative;
                text-decoration: none;
                color: $white;
                transition: color .5s ease;
                transition: ease all .5s;

                &:active {
                    color: $orange;

                    &::after {
                        --scale: 1;
                    }
                }

                &::after {
                    --scale: 0;

                    content: '';
                    position: absolute;
                    left: 0;
                    right: 0;
                    top: 100%;
                    min-width: 3px;
                    height: 3px;
                    background: $orange;
                    transform: scaleX(var(--scale));
                    transform-origin: var(--x) 50%;
                    transition: transform .5s cubic-bezier(.535, .05, .355, 1);

                }

                &:hover {
                    transition-delay: 100ms;
                    color: $orange;
                }

                &:hover::after {
                    transition-delay: 100ms;
                    --scale: 1;
                }

                &:active {
                    color: $orange;

                    &::after {

                        --scale: 1;
                    }
                }

                &.active {
                    color: $orange;
                    // border-bottom: 3px solid $orange;
                    transition: ease all .3s;

                    &::after {
                        --scale: 1;

                        content: '';
                        position: absolute;
                        left: 0;
                        right: 0;
                        top: 100%;
                        height: 3px;
                        background: $orange;
                        transform: scaleX(var(--scale));
                        transform-origin: var(--x) 50%;
                        transition: transform .5s cubic-bezier(.535, .05, .355, 1);
                    }

                    // &::before {
                    //     content: "•" !important;
                    //     position: absolute;
                    //     left: 50%;
                    //     top: -20px;
                    //     color: $orange;
                    //     // font-weight: 900;
                    // }
                }
            }



            .apply-cta-link {
                position: relative;
                margin-right: 20px;

                .apply-link {
                    background-color: none;
                    color: $white;
                    padding: 0.8em 1em;
                    border: 1px solid $orange;
                    transition: all 0.3s ease-in-out;

                    i {
                        margin-right: 8px;
                    }

                    &:hover {
                        background: rgba($white, 10%);

                    }
                }

                span {
                    position: absolute;
                    background-color: $orange;
                    transform-origin: center;
                    transition: transform 0.5s ease;

                    span.bottom,
                    span.top {
                        height: 3px;
                        left: 0;
                        right: 0;
                        transform: scaleX(0);
                    }

                    span.left,
                    span.right {
                        width: 3px;
                        top: 0;
                        bottom: 0;
                        transform: scaleY(0);
                    }

                    span.top {
                        top: -1em;
                    }

                    span.bottom {
                        bottom: -1em;
                    }

                    span.left {
                        left: -1em;
                    }

                    span.right {
                        right: -1em;
                    }
                }


            }
        }

        svg,
        img {
            max-width: 200px;
            flex-grow: 1;
        }
    }

    @include sm {
        padding-top: 0em;
        overflow-x: hidden;
        position: relative;

        .header-wrapper {
            display: none;
        }

        .content-area {
            width: 100vw;
            padding: 0 1em;
            position: fixed;
            top: 0px;
            left: 0px;
            z-index: 200;
            overflow: hidden;
            height: 100vh;
            display: flex;
            flex-direction: row;
            align-items: flex-start;
            justify-content: space-between;
            // background:lime;
            pointer-events: none;

            &::before {
                content: '';
                position: absolute;
                z-index: 3;
                width: 100vw;
                height: 75px;
                background: rgba($blue, 0.9);
                top: 0px;
                left: 0px;
            }

        }

        img {
            position: relative;
            margin-top: 1.5em;
            height: 30px;
            z-index: 5;

        }

        nav.main {
            float: right;
        }

        .menu-items {
            position: absolute;
            right: -100%;
            z-index: 0;
            list-style: none;
            pointer-events: all;
            bottom: 0px;
            width: 100%;
            height: 90%;
            display: flex;
            flex-direction: column;
            align-content: center;
            justify-content: flex-start;
            align-items: center;

            .bang {
                margin-top: 5em;
            }

            font: {
                family: 'Open Sans', sans-serif;
                weight: 600;
            }

            background: rgba($lighter-blue, 0.9);
            text-transform: uppercase;
            padding: 15px;
            transition: right 530ms ease;

            li {
                padding: 8px 10px;
                background: transparent;
                border: 2px solid $orange;

                a {
                    color: $white;
                    font-size: 20pt;

                    i {
                        display: none;
                    }
                }

                // &:not(:first-child) {
                //     border-top: 1px solid black;
                // }
                &+li {
                    margin-top: 1em;
                }
            }

            &.visible {
                right: 0;
                transition: right 430ms ease;
            }
        }

        .mobile-menu-btn {
            display: inline-block;
            position: relative;
            width: 18px;
            height: 14px;
            cursor: pointer;
            pointer-events: all;
            z-index: 20;
            transform: scale(2);
            margin: 2em;
            background: $orange;
            span {
                display: inline-block;
                position: absolute;
                top: 50%;
                transform: translateY(-50%);
                width: 18px;
                height: 2px;
                background-color: white;
                opacity: 1;
                transition: opacity .5s ease;
            }

            &:before,
            &:after {
                content: '';
                display: inline-block;
                width: 18px;
                height: 2px;
                background-color: white;
                position: absolute;
            }

            &:before {
                top: 0;
                transform: rotate(0deg);
                transition: transform .25s ease, top .25s ease .25s;
            }

            &:after {
                bottom: 0;
                transform: rotate(0deg);
                transition: transform .25s ease, bottom .25s ease .25s;
            }

            &.open {

                span {
                    opacity: 0;
                    transition: opacity .5s ease;
                }

                &:before {
                    top: 6px;
                    transform: rotate(45deg);
                    transition: transform .25s ease .25s, top .25s ease;
                }

                &:after {
                    bottom: 6px;
                    transform: rotate(-45deg);
                    transition: transform .25s ease .25s, bottom .25s ease;
                }
            }
        }
    }
}

sources.overview.scss:
.overview {
    position: relative;
    height: auto;
    background: $blue;

    .calltoaction {
        position: relative;
        margin-top: -7.5em;
        min-height: 108vh;
        z-index: 2;
        top: 0px;
        width: 100%;
        background: $lighter-blue;
        display: flex;
        flex-direction: column;
        align-items: flex-end;
        justify-content: center;
        background: url('./img/bridge_v2-min.jpg') no-repeat;
        background-size: 100%;
        background-position: bottom left;

        #illustration {
            background-color: $lighter-blue;
            width: 100%;
            height: auto;
            display: block;
            transform: translate3d(0, 0, 0);
            text-align: center;
            opacity: 1;
            z-index: -1;
            position: absolute;
        }

        .content-wrapper {
            height: auto;
            width: 100%;
            display: flex;
            justify-content: flex-start;

            @include lg {
                width: 90vw;
            }

            .content {
                max-width: 500px;
                padding: 2em 3em;
                border: 6px solid $white;
                background: rgba($blue, 85%);


                p {
                    margin-bottom: 1.5em;
                    line-height: 18pt;
                }
            }
        }
    }

    .main-cards {
        margin-top: -5em;
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        position: relative;
        z-index: 3;
        padding-bottom: 1em;

        @include lg {
            margin-top: -2em;
        }

        .cards {
            display: inherit;
            flex-direction: row;
            flex-wrap: wrap;
            align-items: stretch;
            justify-content: space-around;
            width: 100%;

            .card {
                flex-grow: 1;
                flex: 1 1 30%;
                min-width: 280px;
                min-height: 50px;
                background: linear-gradient(140deg, $lighter-blue, lighten($blue, 5%));
                display: flex;
                flex-direction: column;
                padding: 3em 1.5em 2em 1.5em;
                overflow: hidden;
                position: relative;
                border: 4px solid $blue;
                transition: ease all 230ms;
                color: $white;
                cursor: pointer;

                h4 {
                    font-size: 20pt;
                    line-height: 10pt;
                    margin: 1em 0;
                    text-align: center;
                }

                p {
                    text-align: center;
                    margin: 0 2em;
                    line-height: 16pt;

                    @include lg {
                        margin: 0 1em;

                    }
                }

                .fa-cog {
                    position: absolute;
                    font-size: 11em;
                    line-height: 0pt;
                    left: -.5em;
                    top: 10px;
                    transition: ease all 3s;
                    color: rgba($white, 10%);
                }

                .code-symbol {
                    position: absolute;
                    color: rgba($white, 10%);
                    top: -20px;
                    left: -15px;
                    font-size: 75pt;
                    font-weight: 800;
                    letter-spacing: -8pt;
                    transition: ease all 1.5s;

                }

                .bulbs {
                    transform: rotate(180deg);
                    position: absolute;
                    font-size: 8em;
                    line-height: 0pt;
                    left: 0.7em;
                    top: 1em;
                    transition: ease all 1s;
                    color: rgba($white, 10%);

                    i {
                        position: absolute;

                        &.far {
                            filter: opacity(1);
                            transition: ease all 1s;
                        }

                        &.fas {
                            filter: opacity(0);
                            transition: ease all 1s;

                        }
                    }
                }

                &:hover {
                    border-color: $orange;

                    .fa-cog {
                        transform: rotate(70deg);
                    }

                    .code-symbol {
                        letter-spacing: -10pt;
                        transform: rotate(10deg);
                    }

                    .bulbs {
                        i {
                            transform: translateY(10px);

                            &.far {
                                filter: opacity(0);
                            }

                            &.fas {
                                filter: opacity(1);

                            }
                        }
                    }
                }

                &+.card {
                    margin-left: 1em;
                }
            }
        }
    }

    .beginnings {
        position: relative;
        border-right: 2px solid $orange;

        .about-wrapper {
            display: flex;
            flex-direction: row;
            justify-content: space-between;
            width: 100%;
            position: relative;

            .pics {
                top: 0px;
                background: $orange;
                flex: 1 30%;
                display: flex;
                flex-direction: row;
                align-items: flex-start;
                max-width: 500px;
                align-self: stretch;
                background: url('./img/front-min.jpg') no-repeat;
                background-size: cover;
                background-position: top center;
                background-attachment: scroll;

                @include lg {
                    flex: 1 50%;
                }

            }

            .about {
                position: relative;
                max-width: 700px;
                padding: 3em 0em;
                flex: 1 70%;

                @include lg {
                    flex: 1 50%;
                    padding: 3em 0em 3em 3em;
                }

                p,
                h2 {
                    margin-right: 30px;

                }

                p {
                    line-height: 18pt;
                    margin-top: 1em;

                    a {

                        color: $orange;
                        transition: ease all 230ms;

                        &:hover {
                            color: lighten($orange, 10%);
                        }
                    }

                    &+p {
                        margin-top: 1.5em;
                    }
