    .cool-culture {
        position: relative;
        border-left: 2px solid $orange;

        h2 {
            .head_actually {
                // display: block;
                margin-left: 10px;
                color: rgba($white, 25%);

                @include lg {
                    display: block;
                    margin-left: 0px;
                }
            }
        }

        .cool-wrapper {
            display: flex;
            flex-direction: row;
            justify-content: space-between;
            width: 100%;
            position: relative;

            .pics {
                order: 2;
                top: 0px;
                right: 0px;
                background: $orange;
                flex: 1 30%;
                display: flex;
                flex-direction: row;
                align-items: flex-start;
                max-width: 500px;
                align-self: stretch;
                background: url('./img/randy-min.jpg') no-repeat;
                background-size: cover;
                background-position: top center;
                background-attachment: scroll;

                @include lg {
