                }
            }

            .cool {
                max-width: 700px;
                padding: 3em 0em;
                flex: 1 70%;

                @include lg {
                    flex: 1 50%;
                    padding: 3em 3em 3em 0em;
                }

                p,
                h2 {
                    margin-left: 30px;

                }

                p {
                    line-height: 18pt;
                    margin-top: 1em;

                    a {
                        background: $lighter-blue;
                        padding: 3px 5px;
                        color: $orange;
                        transition: ease all 230ms;

                        &:hover {
                            color: $white;
                        }
                    }

                    &+p {
                        margin-top: 1.5em;
                    }
                }
            }

            &::after {
                position: absolute;
                content: "";
                bottom: 0px;
                height: 2px;
                background: linear-gradient(to right, $orange, $blue);
                width: 100%;
                @include lg {
                                    background: linear-gradient(to right, $orange, $blue 55%);

                }
            }
        }
    }

    .jobs-calltoaction {
        display: flex;
        position: relative;
        overflow: none;
        padding: 3em 0em;

        .jobs-wrapper {
            display: inherit;
            flex-direction: row;
            align-items: center;
            justify-content: space-around;
            width: 100%;
@include sm {
    flex-wrap: wrap;
}
            .content {
                text-align: center;

                .head_orange {
                    margin-right: -10px;
                }

                h3 {
                    margin-top: 1em;

                }
            }

            .jobs-cta {
                button {
                    transform: scale(1.75);
                }
            }
        }

        &::after {
            position: absolute;
            content: "";
            bottom: 0px;
            height: 2px;
            background: -moz-radial-gradient($orange, $blue 75%);
            background: -webkit-radial-gradient($orange, $blue 75%);
            background: radial-gradient($orange, $blue 75%);
            width: 100%;
        }
    }
}

sources.footer.scss:
footer {
    background: darken($blue, 3%);
    border-top: 4px solid lighten($lighter-blue, 5%);
    margin-top: 4em !important;


    .footer-wrapper {
        display: flex;
        flex-direction: row;
        align-items: center;
        justify-content: space-around;
        padding: 2em 0;
        margin: 0 auto;

        .logo {
            width: 200px;

            img {
                max-width: 80px;
            }
        }

        .social {
            width: 200px;
            display: flex;
            flex-direction: row;
            align-content: center;
            justify-content: center;

            a {
                color: white;
                font-size: 20pt;
                transition: ease all 230ms;

                &+a {
                    margin-left: 20px;
                }

                &:hover {
                    color: $orange;
                }
            }
        }

        .address {
            width: 200px;
            font-weight: 300;
            color: rgba($white, 85%);
        }
    }
}
